function slugify(term: string): string {
    return term
        .toLowerCase()
        .replace(/[()\/]/g, '')
        .replace(/\s+/g, '-')
        .replace(/[^a-z0-9-]/g, '')
        .replace(/-+/g, '-')
        .replace(/^-|-$/g, '');
}

